  `wx_nickname` varchar(32) CHARACTER SET utf8mb4 NOT NULL,
  `wx_sex` varchar(6) NOT NULL,
  `wx_openid` varchar(100) NOT NULL,
  `wx_headImg` varchar(300) NOT NULL,
  `up_id` varchar(50) DEFAULT NULL COMMENT '上级ID',
  `create_date` varchar(32) NOT NULL,
  `points` float(13,2) NOT NULL DEFAULT '0.00' COMMENT '积分',
  `money_still` float(7,2) NOT NULL DEFAULT '0.00' COMMENT '剩余余额',
  `money_gift` float(7,2) DEFAULT '0.00' COMMENT '赠送余额',
  `money_used` float(6,2) DEFAULT '0.00' COMMENT '花费余额',
  `rank` varchar(10) NOT NULL DEFAULT 'normal' COMMENT '状态',
  `status` varchar(10) NOT NULL DEFAULT 'green' COMMENT '不知道干啥用的字段',
  `QRcodeURL` varchar(200) DEFAULT NULL,
  `address` varchar(32) DEFAULT NULL COMMENT '地址',
  `real_card_id` int(11) DEFAULT NULL COMMENT '实体卡号\n',
  `tel` varchar(16) DEFAULT NULL COMMENT '电话',
  `real_name` varchar(16) DEFAULT NULL COMMENT '真实姓名',
  `lock` varchar(6) DEFAULT NULL,
  `pwd` varchar(32) DEFAULT '000000' COMMENT '会员卡密码',
  `userinfo_lock` varchar(6) DEFAULT NULL,
  `expdate` datetime DEFAULT NULL,
  `cardtype` varchar(32) DEFAULT '0' COMMENT '会员卡类型(等级)',
  `remark` varchar(1000) DEFAULT NULL,
  `blog` varchar(255) DEFAULT NULL COMMENT '判断是否激活 null 否 1.是的',
  `birthday` varchar(255) DEFAULT NULL COMMENT '生日',
  `car` varchar(255) DEFAULT NULL COMMENT '车牌号',
  `store_id` varchar(255) DEFAULT NULL,
  `qd_num` varchar(255) DEFAULT '0' COMMENT '签到连续天数',
  `qd_points` varchar(255) DEFAULT '0' COMMENT '签到获得积分',
  `point_used` float(9,2) DEFAULT '0.00' COMMENT '已用积分',
  `my_daijin` varchar(255) DEFAULT NULL,
  `up_points` varchar(255) DEFAULT '0' COMMENT '推广积分',
  `up_money_gift` varchar(255) DEFAULT '0' COMMENT '下级收益赠送金额',
  PRIMARY KEY (`account_id`)
) ENGINE=InnoDB AUTO_INCREMENT=60068 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC COMMENT='德盟汇';

